mapper.select:
SELECT id, user_id, status, write_date, is_lock, IF(is_lock = 1, NULL, mood_id) AS mood_id, IF(is_lock = 1, NULL, keyword) AS keyword, IF(is_lock = 1, NULL, site) AS site, IF(is_lock = 1, NULL, remark) AS remark, /*IF(is_lock = 1, NULL, created) AS created, IF(is_lock = 1, NULL, updated) AS updated*/ score, created, updated FROM tb_diary WHERE status >= 0
AND user_id = #{user.id}
AND mood_id = #{diaryPage.moodId}
AND write_date > #{diaryPage.startTime}
AND write_date < #{diaryPage.endTime}
ORDER BY write_date DESC LIMIT #{diaryPage.pageIndex}, #{diaryPage.pageSize}

SELECT COUNT(*)
FROM tb_diary
WHERE status >= 0

	AND user_id = #{user.id}


	AND mood_id = #{diaryPage.moodId}


	AND write_date > #{diaryPage.startTime}


	AND write_date < #{diaryPage.endTime}
